function [X, Y] = loadSpam(names, messages, spam, vocabList, vocabulario)    X = [];    messagesIND = find(spam == 1);    spamMsg = cell(rows(messagesIND),1);    counter = 1;    for i = 1:rows(messages)      if (any(messagesIND == i))        spamMsg{counter} = messages{i};        counter++;      endif    endfor    for j = 1:rows(spamMsg)        wor_s = processEmail(spamMsg{j});      Xi = zeros(1, length(vocabList));      while ~isempty(wor_s)         [str,wor_s] = strtok(wor_s,[' ']);         if isfield(vocabulario,str)          Xi(vocabulario.(str))=1;         endif      endwhile      X = [X;Xi];    endfor    Y = ones(rows(X), 1);    save spam.mat X Y;endfunction