function [X_s, y_s] = loadSpam(names, messages, spam, vocabList, vocabulario)    X_s = [];    messagesIND = find(spam == 1);    spamMsg = cell(rows(messagesIND),1);    counter = 1;    for i = 1:rows(messages)      if (any(messagesIND == i))        spamMsg{counter} = messages{i};        counter++;      endif    endfor    for j = 1:rows(spamMsg)        wor_s = processEmail(spamMsg{j});      Xi = zeros(1, length(vocabList));      while ~isempty(wor_s)         [str,wor_s] = strtok(wor_s,[' ']);         if isfield(vocabulario,str)          Xi(vocabulario.(str))=1;         endif      endwhile      X_s = [X_s;Xi];    endfor    y_s = repmat(2, rows(X_s), 1);endfunction